  year = now.getFullYear();
  month = now.getMonth();
  week = now.getDay();
  date = now.getDate();

  const e = document.getElementById("card-widget-calendar");
  if (e) {
    const isLeapYear = (year % 4 === 0 && year % 100 !== 0) || year % 400 === 0;
    weekStr = ["Dom", "Lun", "Mar", "Mié", "Jue", "Vie", "Sáb"][week];
    const monthData = [
      { month: "Ene", days: 31 },
